import axios from "axios"
import { useState } from "react"

export default function ListOfBooks() {
    const [link,setLink] = useState("https://api.artic.edu/api/v1/artworks?page=1&limit=10")
    const [pagination,setPagination] = useState({})
    const [listOfBooks,setListOfBooks] = useState([])
    axios.get(link)
    .then((res)=>{
        setPagination(res.data.pagaination)
        setListOfBooks(res.data.data)
    })
  return (
    <div>
      {listOfBooks.map((book)=>
      <div class="card">
        <img src={`https://www.artic.edu/iiif/2/${book.image_id}/full/843,/0/default.jpg`} class="card-img-top" alt="Book Cover" width="200px" height="200px"/>
        <div class="card-body">
          <h5 class="card-title">{book.title}</h5>
          <a href="#" class="btn btn-primary">Get Book</a>
        </div>
      </div>
      )}
    </div>
  )
}
