import '../styles/comments.css'
export default function Comments() {
  return (
    <div>
      <form className="comments-form p-3 my-3">
        <input type="text" className="form-control my-2" placeholder="Enter your name" required autoFocus/>
        <input type="email" className="form-control my-2" placeholder="Enter your email" required/>
        <textarea rows="6" cols="40" className="form-control my-2" required></textarea>
        <input type='submit' className='btn btn-danger'/>
      </form>
    </div>
  )
}
